import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const RegisterPage = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    password2: '', // Untuk konfirmasi password
  });
  const [error, setError] = useState('');
  const { register } = useAuth();
  const navigate = useNavigate();

  const { username, password, password2 } = formData;

  const onChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();
    if (password !== password2) {
      setError('Password tidak cocok');
      return;
    }
    setError('');
    try {
      await register({ username, password });
      // Navigasi sudah diurus di dalam context
    } catch (err) {
      setError(err.message || 'Pendaftaran gagal!');
    }
  };

  return (
    <div className="form-container">
      <h1>Buat Akun Baru</h1>
      {error && <div className="message error">{error}</div>}
      <form onSubmit={onSubmit}>
        <div className="form-group">
          <label htmlFor="username">Username</label>
          <input type="text" id="username" name="username" value={username} onChange={onChange} required />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input type="password" id="password" name="password" value={password} onChange={onChange} required minLength="6" />
        </div>
        <div className="form-group">
          <label htmlFor="password2">Konfirmasi Password</label>
          <input type="password" id="password2" name="password2" value={password2} onChange={onChange} required minLength="6" />
        </div>
        <button type="submit" className="btn-block">Daftar</button>
      </form>
      <p className="sub-text">
        Sudah punya akun? <Link to="/login">Login di sini</Link>
      </p>
    </div>
  );
};

export default RegisterPage;
